import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import "./badge.css"

const badgeVariants = cva(
  "inline-flex items-center justify-center overflow-hidden whitespace-nowrap rounded-full [&_svg]:pointer-events-none [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        brand: "badge-brand",
        neutral: "badge-neutral",
        success: "badge-success",
        danger: "badge-danger",
        warning: "badge-warning",
      },
      size: {
        sm: "min-w-5 gap-[var(--layout-gap-xs)] px-[var(--layout-padding-sm)] py-[var(--layout-padding-2xs)] text-content-caption [&_svg]:size-3 [&_.badge-dot]:size-1.5",
        md: "min-w-6 gap-[var(--layout-gap-sm)] px-[var(--layout-padding-md)] py-[var(--layout-padding-xs)] text-content-caption [&_svg]:size-3 [&_.badge-dot]:size-1.5",
        lg: "min-w-7 gap-[var(--layout-gap-sm)] px-[var(--layout-padding-lg)] py-[var(--layout-padding-xs)] text-content-note [&_svg]:size-3.5 [&_.badge-dot]:size-2",
      },
      type: {
        fill: "",
        light: "badge-light",
        dash: "badge-dash",
      },
    },
    defaultVariants: {
      variant: "neutral",
      size: "md",
      type: "fill",
    },
  }
)

function BadgeDot({ className }: { className?: string }) {
  return <span className={cn("badge-dot shrink-0", className)} />
}

function Badge({
  className,
  variant,
  size,
  type,
  isFilled = false,
  children,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof badgeVariants> & {
    isFilled?: boolean
  }) {
  return (
    <span
      data-slot="badge"
      className={cn(
        badgeVariants({ variant, size, type, className }),
        isFilled && "badge-filled"
      )}
      {...props}
    >
      {children}
    </span>
  )
}

export { Badge, BadgeDot, badgeVariants }

/* ================================================================
 * BADGE — Variant & Type Styles
 * Structure (flex, min-w, gap, padding, radius) lives in badge.tsx via cva.
 * Theming (colors, borders, dot) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Variant tokens
 * Each variant sets scoped custom properties.
 * -------------------------------- */

.badge-brand {
  --badge-overlay: var(--overlay-brand-default);
  --badge-bg: var(--background-brand-strong-default);
  --badge-text: var(--text-brand-moderate);
  --badge-text-filled: var(--text-fixed);
  --badge-border: var(--border-brand-lighter);
}

.badge-neutral {
  --badge-overlay: var(--overlay-neutral-default);
  --badge-bg: var(--background-neutral-strong-default);
  --badge-text: var(--text-base-moderate);
  --badge-text-filled: var(--text-inverse);
  --badge-border: var(--border-neutral-lighter);
}

.badge-success {
  --badge-overlay: var(--overlay-success-default);
  --badge-bg: var(--background-success-strong-default);
  --badge-text: var(--text-success-moderate);
  --badge-text-filled: var(--text-fixed);
  --badge-border: var(--border-success-lighter);
}

.badge-danger {
  --badge-overlay: var(--overlay-danger-default);
  --badge-bg: var(--background-danger-strong-default);
  --badge-text: var(--text-danger-moderate);
  --badge-text-filled: var(--text-fixed);
  --badge-border: var(--border-danger-lighter);
}

.badge-warning {
  --badge-overlay: var(--overlay-warning-default);
  --badge-bg: var(--background-warning-strong-default);
  --badge-text: var(--text-warning-moderate);
  --badge-text-filled: var(--text-fixed);
  --badge-border: var(--border-warning-lighter);
}

/* --------------------------------
 * Default state (non-filled)
 * -------------------------------- */
.badge-brand,
.badge-neutral,
.badge-success,
.badge-danger,
.badge-warning {
  background: var(--badge-overlay);
  color: var(--badge-text);
}

/* --------------------------------
 * Filled state
 * -------------------------------- */
.badge-filled {
  background: var(--badge-bg);
  color: var(--badge-text-filled);
}

/* --------------------------------
 * Type — light (solid border)
 * -------------------------------- */
.badge-light {
  border: var(--layout-border-thin) solid var(--badge-border);
}

/* --------------------------------
 * Type — dash (dashed border)
 * -------------------------------- */
.badge-dash {
  border: var(--layout-border-thin) dashed var(--badge-border);
}

/* --------------------------------
 * Dot
 * -------------------------------- */
.badge-dot {
  border-radius: var(--layout-radius-full);
  background: var(--badge-bg);
}

.badge-filled .badge-dot {
  background: var(--badge-text-filled);
}
