import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import {
  Sparkles,
  Info,
  TriangleAlert,
  CircleCheck,
} from "lucide-react"

import { cn } from "@/lib/utils"
import "./banner-info.css"

const bannerInfoVariants = cva(
  "flex items-start gap-[var(--layout-gap-md)] rounded-[var(--layout-radius-xl)] p-[var(--layout-padding-lg)] text-content-note [&_svg]:shrink-0 [&_svg]:h-[var(--layout-size-sm)] [&_svg]:w-[var(--layout-size-sm)]",
  {
    variants: {
      variant: {
        brand: "banner-info-brand",
        neutral: "banner-info-neutral",
        danger: "banner-info-danger",
        success: "banner-info-success",
        warning: "banner-info-warning",
      },
    },
    defaultVariants: {
      variant: "neutral",
    },
  }
)

const variantIcons = {
  brand: Sparkles,
  neutral: Info,
  danger: TriangleAlert,
  success: CircleCheck,
  warning: TriangleAlert,
} as const

function BannerInfo({
  className,
  variant = "neutral",
  withIcon = true,
  children,
  ...props
}: React.ComponentProps<"div"> &
  VariantProps<typeof bannerInfoVariants> & {
    withIcon?: boolean
  }) {
  const Icon = variant ? variantIcons[variant] : variantIcons.neutral

  return (
    <div
      data-slot="banner-info"
      role="status"
      className={cn(bannerInfoVariants({ variant, className }))}
      {...props}
    >
      {withIcon && <Icon aria-hidden="true" />}
      <div className="flex-1 min-w-0">{children}</div>
    </div>
  )
}

export { BannerInfo, bannerInfoVariants }

/* ================================================================
 * BANNER INFO — Variant Styles
 * Structure (flex, padding, gap, radius) lives in banner-info.tsx via cva.
 * Theming (colors, borders, text) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Variant tokens
 * Each variant sets scoped custom properties.
 * -------------------------------- */

.banner-info-brand {
  --banner-bg: var(--overlay-brand-default);
  --banner-border: var(--border-brand-lighter);
  --banner-text: var(--text-brand-moderate);
  --banner-icon: var(--icon-brand-moderate);
}

.banner-info-neutral {
  --banner-bg: var(--background-neutral-faint-default);
  --banner-border: var(--border-default);
  --banner-text: var(--text-base-bolder);
  --banner-icon: var(--icon-neutral-moderate);
}

.banner-info-danger {
  --banner-bg: var(--overlay-danger-default);
  --banner-border: var(--border-danger-lighter);
  --banner-text: var(--text-danger-moderate);
  --banner-icon: var(--icon-danger-moderate);
}

.banner-info-success {
  --banner-bg: var(--overlay-success-default);
  --banner-border: var(--border-success-lighter);
  --banner-text: var(--text-success-moderate);
  --banner-icon: var(--icon-success-moderate);
}

.banner-info-warning {
  --banner-bg: var(--overlay-warning-default);
  --banner-border: var(--border-warning-lighter);
  --banner-text: var(--text-warning-moderate);
  --banner-icon: var(--icon-warning-moderate);
}

/* --------------------------------
 * Base — applied to all variants
 * -------------------------------- */
.banner-info-brand,
.banner-info-neutral,
.banner-info-danger,
.banner-info-success,
.banner-info-warning {
  background: var(--banner-bg);
  border: var(--layout-border-thin) solid var(--banner-border);
  color: var(--banner-text);
}

/* --------------------------------
 * Icon color — separate from text
 * -------------------------------- */
.banner-info-brand svg,
.banner-info-neutral svg,
.banner-info-danger svg,
.banner-info-success svg,
.banner-info-warning svg {
  color: var(--banner-icon);
}
