import * as React from "react"
import * as CheckboxPrimitive from "@radix-ui/react-checkbox"
import { cva, type VariantProps } from "class-variance-authority"
import { Check, Minus } from "lucide-react"

import { cn } from "@/lib/utils"
import "./checkbox.css"

const checkboxVariants = cva(
  "checkbox-indicator inline-flex items-center justify-center shrink-0",
  {
    variants: {
      size: {
        sm: "h-[var(--layout-size-xs)] w-[var(--layout-size-xs)] rounded-[var(--layout-radius-sm)]",
        md: "h-[var(--layout-size-sm)] w-[var(--layout-size-sm)] rounded-[var(--layout-radius-md)]",
      },
    },
    defaultVariants: {
      size: "sm",
    },
  }
)

function Checkbox({
  className,
  size = "sm",
  label,
  description,
  disabled,
  ...props
}: React.ComponentProps<typeof CheckboxPrimitive.Root> &
  VariantProps<typeof checkboxVariants> & {
    label?: string
    description?: string
  }) {
  const id = React.useId()

  return (
    <div
      data-slot="checkbox"
      data-disabled={disabled || undefined}
      className={cn("flex items-start gap-[var(--layout-gap-md)]", className)}
    >
      <div className="flex items-center py-[var(--layout-padding-2xs)]">
        <CheckboxPrimitive.Root
          id={id}
          className={cn(checkboxVariants({ size }))}
          disabled={disabled}
          {...props}
        >
          <CheckboxPrimitive.Indicator className="checkbox-icon flex items-center justify-center">
            <Check
              className={cn(
                "checkbox-check",
                size === "sm" ? "size-3" : "size-3.5"
              )}
              strokeWidth={3}
            />
            <Minus
              className={cn(
                "checkbox-minus",
                size === "sm" ? "size-3" : "size-3.5"
              )}
              strokeWidth={3}
            />
          </CheckboxPrimitive.Indicator>
        </CheckboxPrimitive.Root>
      </div>
      {label && (
        <label
          htmlFor={id}
          className={cn(
            "flex flex-col",
            disabled ? "cursor-default" : "cursor-pointer"
          )}
        >
          <span
            className={cn(
              "font-accent checkbox-label",
              size === "sm" ? "text-content-note" : "text-content-body"
            )}
          >
            {label}
          </span>
          {description && (
            <span className="text-content-note checkbox-description">
              {description}
            </span>
          )}
        </label>
      )}
    </div>
  )
}

export { Checkbox, checkboxVariants }

/* ================================================================
 * CHECKBOX — Indicator & State Styles
 * Structure (flex, size, gap, radius) lives in checkbox.tsx via cva.
 * Theming (colors, borders, states) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Indicator — base
 * -------------------------------- */
.checkbox-indicator {
  border: var(--layout-border-thin) solid var(--border-default);
  overflow: hidden;
  cursor: pointer;
}

@media (prefers-reduced-motion: no-preference) {
  .checkbox-indicator {
    transition:
      background 150ms ease,
      border-color 150ms ease;
  }
}

/* --------------------------------
 * Indicator — unchecked states
 * -------------------------------- */
.checkbox-indicator[data-state="unchecked"] {
  background: var(--background-neutral-faint-default);
}
.checkbox-indicator[data-state="unchecked"]:hover {
  background: var(--background-neutral-faint-hover);
}
.checkbox-indicator[data-state="unchecked"]:active {
  background: var(--background-neutral-faint-pressed);
}

/* --------------------------------
 * Indicator — checked states
 * -------------------------------- */
.checkbox-indicator[data-state="checked"] {
  background: var(--background-brand-strong-default);
}
.checkbox-indicator[data-state="checked"]:hover {
  background: var(--background-brand-strong-hover);
}
.checkbox-indicator[data-state="checked"]:active {
  background: var(--background-brand-strong-pressed);
}

/* --------------------------------
 * Indicator — indeterminate states
 * -------------------------------- */
.checkbox-indicator[data-state="indeterminate"] {
  background: var(--background-brand-strong-default);
}
.checkbox-indicator[data-state="indeterminate"]:hover {
  background: var(--background-brand-strong-hover);
}
.checkbox-indicator[data-state="indeterminate"]:active {
  background: var(--background-brand-strong-pressed);
}

/* --------------------------------
 * Indicator — disabled
 * -------------------------------- */
.checkbox-indicator[data-disabled] {
  background: var(--background-disabled);
  border-color: var(--border-disabled);
  pointer-events: none;
  cursor: default;
}

/* --------------------------------
 * Focus ring (matches Radio/Button pattern)
 * -------------------------------- */
.checkbox-indicator:focus-visible {
  outline: none;
  box-shadow: 0 0 0 2px var(--background-base), 0 0 0 4px var(--border-selected);
}

/* --------------------------------
 * Icon visibility — show check or minus based on state
 * -------------------------------- */
.checkbox-icon {
  color: var(--text-fixed);
}

.checkbox-indicator[data-disabled] .checkbox-icon {
  color: var(--text-disabled);
}

/* Default: hide both icons */
.checkbox-check,
.checkbox-minus {
  display: none;
}

/* Show check when checked */
.checkbox-indicator[data-state="checked"] .checkbox-check {
  display: block;
}

/* Show minus when indeterminate */
.checkbox-indicator[data-state="indeterminate"] .checkbox-minus {
  display: block;
}

/* --------------------------------
 * Text — label & description
 * -------------------------------- */
.checkbox-label {
  color: var(--text-base-strong);
}
.checkbox-description {
  color: var(--text-base-medium);
}

/* --------------------------------
 * Text — disabled
 * -------------------------------- */
[data-slot="checkbox"][data-disabled] .checkbox-label,
[data-slot="checkbox"][data-disabled] .checkbox-description {
  color: var(--text-disabled);
}
[data-slot="checkbox"][data-disabled] label {
  cursor: default;
}
