import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import "./input.css"

/* ------------------------------------------------------------------ */
/*  CVA                                                                */
/* ------------------------------------------------------------------ */

const inputVariants = cva(
  "input-base flex items-center overflow-hidden w-full",
  {
    variants: {
      variant: {
        default: "",
        destructive: "input-destructive",
        success: "input-success",
      },
      size: {
        sm: "h-[var(--layout-size-lg)] gap-[var(--layout-gap-sm)] px-[var(--layout-padding-md)] rounded-[var(--layout-radius-lg)] text-content-note [&_svg]:size-4",
        md: "h-[var(--layout-size-xl)] gap-[var(--layout-gap-sm)] px-[var(--layout-padding-md)] rounded-[var(--layout-radius-lg)] text-content-note [&_svg]:size-[18px]",
        lg: "h-[var(--layout-size-2xl)] gap-[var(--layout-gap-md)] px-[var(--layout-padding-lg)] rounded-[var(--layout-radius-xl)] text-content-body [&_svg]:size-5",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "md",
    },
  }
)

const inputHintVariants = cva("input-hint text-content-note", {
  variants: {
    variant: {
      default: "",
      destructive: "input-hint-destructive",
      success: "input-hint-success",
    },
  },
  defaultVariants: {
    variant: "default",
  },
})

/* ------------------------------------------------------------------ */
/*  Input                                                              */
/* ------------------------------------------------------------------ */

function Input({
  className,
  variant,
  size,
  leading,
  trailing,
  disabled,
  ref,
  ...props
}: Omit<React.ComponentProps<"input">, "size"> &
  VariantProps<typeof inputVariants> & {
    leading?: React.ReactNode
    trailing?: React.ReactNode
  }) {
  return (
    <div
      data-slot="input"
      data-disabled={disabled || undefined}
      className={cn(inputVariants({ variant, size, className }))}
    >
      {leading && (
        <span
          data-slot="input-leading"
          className="input-leading shrink-0 flex items-center"
        >
          {leading}
        </span>
      )}
      <input
        ref={ref}
        disabled={disabled}
        className="input-native flex-1 min-w-0 bg-transparent outline-none"
        {...props}
      />
      {trailing && (
        <span
          data-slot="input-trailing"
          className="input-trailing shrink-0 flex items-center"
        >
          {trailing}
        </span>
      )}
    </div>
  )
}

/* ------------------------------------------------------------------ */
/*  InputField                                                         */
/* ------------------------------------------------------------------ */

function InputField({
  className,
  children,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="input-field"
      className={cn(
        "flex flex-col gap-[var(--layout-gap-md)] w-full",
        className
      )}
      {...props}
    >
      {children}
    </div>
  )
}

/* ------------------------------------------------------------------ */
/*  InputLabel                                                         */
/* ------------------------------------------------------------------ */

function InputLabel({
  className,
  required,
  children,
  ...props
}: React.ComponentProps<"label"> & { required?: boolean }) {
  return (
    <label
      data-slot="input-label"
      className={cn(
        "input-label inline-flex items-center gap-[var(--layout-gap-xs)] text-content-note font-accent",
        className
      )}
      {...props}
    >
      {children}
      {required && <span className="input-label-asterisk">*</span>}
    </label>
  )
}

/* ------------------------------------------------------------------ */
/*  InputHint                                                          */
/* ------------------------------------------------------------------ */

function InputHint({
  className,
  variant,
  children,
  ...props
}: React.ComponentProps<"p"> & VariantProps<typeof inputHintVariants>) {
  return (
    <p
      data-slot="input-hint"
      className={cn(inputHintVariants({ variant, className }))}
      {...props}
    >
      {children}
    </p>
  )
}

export {
  Input,
  InputField,
  InputLabel,
  InputHint,
  inputVariants,
  inputHintVariants,
}

/* ================================================================
 * INPUT — Styles
 * Structure (flex, gap, padding, radius) lives in input.tsx via cva.
 * Theming (colors, borders, focus ring) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Input — base container
 * -------------------------------- */
.input-base {
  background: var(--background-neutral-faint-default);
  border: var(--layout-border-thin) solid var(--border-default);
  color: var(--text-base-strong);
}

@media (prefers-reduced-motion: no-preference) {
  .input-base {
    transition:
      border-color 150ms ease,
      box-shadow 150ms ease;
  }
}

.input-base:focus-within {
  outline: none;
  box-shadow: 0 0 0 2px var(--shadow-brand-moderate);
}

/* --------------------------------
 * Input — native input element
 * -------------------------------- */
.input-native {
  color: inherit;
  font: inherit;
}

.input-native::placeholder {
  color: var(--text-base-moderate);
}

/* --------------------------------
 * Input — disabled
 * -------------------------------- */
.input-base[data-disabled] {
  background: var(--background-disabled);
  border-color: var(--border-disabled);
  color: var(--text-disabled);
  cursor: not-allowed;
  pointer-events: none;
}

.input-base[data-disabled] .input-native::placeholder {
  color: var(--text-disabled);
}

/* --------------------------------
 * Input — leading / trailing icons
 * -------------------------------- */
.input-leading {
  color: var(--icon-neutral-moderate);
}

.input-trailing {
  color: var(--icon-neutral-moderate);
}

.input-base[data-disabled] .input-leading,
.input-base[data-disabled] .input-trailing {
  color: var(--text-disabled);
}

/* --------------------------------
 * Input — destructive variant
 * -------------------------------- */
.input-destructive {
  border-color: var(--border-danger-strong);
}

.input-destructive:focus-within {
  box-shadow: 0 0 0 2px var(--shadow-danger-moderate);
}

.input-destructive .input-trailing {
  color: var(--icon-danger-moderate);
}

/* --------------------------------
 * Input — success variant
 * -------------------------------- */
.input-success {
  border-color: var(--border-success-strong);
}

.input-success:focus-within {
  box-shadow: 0 0 0 2px var(--shadow-success-moderate);
}

.input-success .input-trailing {
  color: var(--icon-success-moderate);
}

/* --------------------------------
 * InputLabel
 * -------------------------------- */
.input-label {
  color: var(--text-base-strong);
}

.input-label-asterisk {
  color: var(--text-danger-moderate);
}

/* --------------------------------
 * InputHint
 * -------------------------------- */
.input-hint {
  color: var(--text-base-moderate);
}

.input-hint-destructive {
  color: var(--text-danger-moderate);
}

.input-hint-success {
  color: var(--text-success-moderate);
}
