import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import "./tag.css"

const tagVariants = cva(
  "inline-flex items-center overflow-hidden whitespace-nowrap font-accent [&_svg]:pointer-events-none [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        brand: "tag-brand",
        neutral: "tag-neutral",
        success: "tag-success",
        danger: "tag-danger",
        warning: "tag-warning",
      },
      size: {
        sm: "gap-[var(--layout-gap-xs)] px-[var(--layout-padding-sm)] py-[var(--layout-padding-2xs)] rounded-[var(--layout-radius-sm)] text-content-caption [&_svg]:size-3 [&_.tag-dot]:size-1.5",
        md: "gap-[var(--layout-gap-sm)] px-[var(--layout-padding-md)] py-[var(--layout-padding-xs)] rounded-[var(--layout-radius-md)] text-content-caption [&_svg]:size-3 [&_.tag-dot]:size-2",
        lg: "gap-[var(--layout-gap-sm)] px-[var(--layout-padding-md)] py-[var(--layout-padding-xs)] rounded-[var(--layout-radius-lg)] text-content-note [&_svg]:size-3.5 [&_.tag-dot]:size-2",
      },
      type: {
        fill: "tag-fill",
        dash: "tag-dash",
        emphasis: "tag-emphasis",
        ghost: "tag-ghost",
      },
    },
    defaultVariants: {
      variant: "neutral",
      size: "sm",
      type: "fill",
    },
  }
)

function TagDot({ className }: { className?: string }) {
  return <span className={cn("tag-dot shrink-0", className)} />
}

function TagClose({
  className,
  children,
  ...props
}: React.ComponentProps<"button">) {
  return (
    <button
      data-slot="tag-close"
      type="button"
      className={cn(
        "tag-close shrink-0 cursor-pointer inline-flex items-center justify-center",
        className
      )}
      {...props}
    >
      {children ?? (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          strokeLinecap="round"
          strokeLinejoin="round"
        >
          <path d="M18 6 6 18" />
          <path d="m6 6 12 12" />
        </svg>
      )}
    </button>
  )
}

function Tag({
  className,
  variant,
  size,
  type,
  children,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof tagVariants>) {
  return (
    <span
      data-slot="tag"
      className={cn(tagVariants({ variant, size, type, className }))}
      {...props}
    >
      {children}
    </span>
  )
}

export { Tag, TagDot, TagClose, tagVariants }

/* ================================================================
 * TAG — Variant & Type Styles
 * Structure (flex, gap, padding, radius) lives in tag.tsx via cva.
 * Theming (colors, borders, dot) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Variant tokens
 * Each variant sets scoped custom properties.
 * -------------------------------- */

.tag-brand {
  --tag-bg-faint: var(--background-brand-faint-default);
  --tag-bg-lighter: var(--background-brand-lighter-default);
  --tag-text: var(--text-brand-moderate);
  --tag-border-variant: var(--border-brand-lighter);
  --tag-dot-color: var(--background-brand-strong-default);
}

.tag-neutral {
  --tag-bg-faint: var(--background-neutral-faint-default);
  --tag-bg-lighter: var(--background-neutral-lighter-default);
  --tag-text: var(--text-base-strong);
  --tag-border-variant: var(--border-default);
  --tag-dot-color: var(--background-neutral-strong-default);
}

.tag-success {
  --tag-bg-faint: var(--background-success-faint-default);
  --tag-bg-lighter: var(--background-success-lighter-default);
  --tag-text: var(--text-success-moderate);
  --tag-border-variant: var(--border-success-lighter);
  --tag-dot-color: var(--background-success-strong-default);
}

.tag-danger {
  --tag-bg-faint: var(--background-danger-faint-default);
  --tag-bg-lighter: var(--background-danger-lighter-default);
  --tag-text: var(--text-danger-moderate);
  --tag-border-variant: var(--border-danger-lighter);
  --tag-dot-color: var(--background-danger-strong-default);
}

.tag-warning {
  --tag-bg-faint: var(--background-warning-faint-default);
  --tag-bg-lighter: var(--background-warning-lighter-default);
  --tag-text: var(--text-warning-moderate);
  --tag-border-variant: var(--border-warning-lighter);
  --tag-dot-color: var(--background-warning-strong-default);
}

/* --------------------------------
 * All variants share text color
 * -------------------------------- */
.tag-brand,
.tag-neutral,
.tag-success,
.tag-danger,
.tag-warning {
  color: var(--tag-text);
}

/* --------------------------------
 * Type — fill (colored bg + colored border, solid)
 * -------------------------------- */
.tag-fill {
  background: var(--tag-bg-faint);
  border: var(--layout-border-thin) solid var(--tag-border-variant);
}

/* --------------------------------
 * Type — dash (colored bg + colored border, dashed)
 * -------------------------------- */
.tag-dash {
  background: var(--tag-bg-faint);
  border: var(--layout-border-thin) dashed var(--tag-border-variant);
}

/* --------------------------------
 * Type — emphasis (lighter bg + default border)
 * -------------------------------- */
.tag-emphasis {
  background: var(--background-neutral-lighter-default);
  border: var(--layout-border-thin) solid var(--border-default);
}

/* --------------------------------
 * Type — ghost (no bg, default border)
 * -------------------------------- */
.tag-ghost {
  border: var(--layout-border-thin) solid var(--border-default);
  color: var(--text-base-strong);
}

.tag-ghost > svg {
  color: var(--tag-text);
}

/* --------------------------------
 * Dot
 * -------------------------------- */
.tag-dot {
  border-radius: var(--layout-radius-full);
  background: var(--tag-dot-color);
}

/* --------------------------------
 * Close button
 * -------------------------------- */
.tag-close {
  color: inherit;
  opacity: 0.7;
}

.tag-close:hover {
  opacity: 1;
}
