import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import "./textarea.css"

/* ------------------------------------------------------------------ */
/*  CVA                                                                */
/* ------------------------------------------------------------------ */

const textareaVariants = cva(
  "textarea-base flex w-full text-content-note font-regular resize-y",
  {
    variants: {
      variant: {
        default: "",
        destructive: "textarea-destructive",
        success: "textarea-success",
      },
      size: {
        sm: "min-h-[100px] p-[var(--layout-padding-md)] rounded-[var(--layout-radius-lg)] text-content-note",
        md: "min-h-[130px] p-[var(--layout-padding-md)] rounded-[var(--layout-radius-lg)] text-content-note",
        lg: "min-h-[166px] p-[var(--layout-padding-lg)] rounded-[var(--layout-radius-xl)] text-content-body",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "md",
    },
  }
)

/* ------------------------------------------------------------------ */
/*  Textarea                                                           */
/* ------------------------------------------------------------------ */

function Textarea({
  className,
  variant,
  size,
  ...props
}: React.ComponentProps<"textarea"> &
  VariantProps<typeof textareaVariants>) {
  return (
    <textarea
      data-slot="textarea"
      className={cn(textareaVariants({ variant, size, className }))}
      {...props}
    />
  )
}

export { Textarea, textareaVariants }

/* ================================================================
 * TEXTAREA â€” Styles
 * Structure (flex, padding, radius, min-height) lives in textarea.tsx via cva.
 * Theming (colors, borders, focus ring) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Base
 * -------------------------------- */
.textarea-base {
  background: var(--background-neutral-faint-default);
  border: var(--layout-border-thin) solid var(--border-default);
  color: var(--text-base-strong);
}

@media (prefers-reduced-motion: no-preference) {
  .textarea-base {
    transition:
      border-color 150ms ease,
      box-shadow 150ms ease;
  }
}

/* --------------------------------
 * Placeholder
 * -------------------------------- */
.textarea-base::placeholder {
  color: var(--text-base-medium);
}

/* --------------------------------
 * Hover
 * -------------------------------- */
.textarea-base:hover:not(:disabled) {
  background: var(--background-neutral-faint-hover);
}

/* --------------------------------
 * Focus
 * -------------------------------- */
.textarea-base:focus-visible {
  outline: none;
  box-shadow: 0 0 0 2px var(--shadow-brand-moderate);
}

/* --------------------------------
 * Disabled
 * -------------------------------- */
.textarea-base:disabled {
  background: var(--background-disabled);
  color: var(--text-disabled);
  border-color: var(--border-disabled);
  cursor: not-allowed;
  resize: none;
}

.textarea-base:disabled::placeholder {
  color: var(--text-disabled);
}

/* --------------------------------
 * Destructive variant
 * -------------------------------- */
.textarea-destructive {
  border-color: var(--border-danger-strong);
}

.textarea-destructive:focus-visible {
  box-shadow: 0 0 0 2px var(--shadow-danger-moderate);
}

/* --------------------------------
 * Success variant
 * -------------------------------- */
.textarea-success {
  border-color: var(--border-success-strong);
}

.textarea-success:focus-visible {
  box-shadow: 0 0 0 2px var(--shadow-success-moderate);
}
