import * as React from "react"
import * as SwitchPrimitive from "@radix-ui/react-switch"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import "./toggle.css"

const toggleVariants = cva(
  "toggle-track inline-flex items-center shrink-0 rounded-[var(--layout-radius-full)] p-[var(--layout-padding-2xs)] transition-colors cursor-pointer",
  {
    variants: {
      size: {
        sm: "h-[var(--layout-size-sm)] w-9",
        md: "h-[var(--layout-size-md)] w-11",
      },
    },
    defaultVariants: {
      size: "sm",
    },
  }
)

const thumbVariants = cva(
  "toggle-thumb block rounded-full",
  {
    variants: {
      size: {
        sm: "h-[var(--layout-size-xs)] w-[var(--layout-size-xs)] data-[state=checked]:translate-x-[14px]",
        md: "h-[var(--layout-size-sm)] w-[var(--layout-size-sm)] data-[state=checked]:translate-x-[18px]",
      },
    },
    defaultVariants: {
      size: "sm",
    },
  }
)

function Toggle({
  className,
  size = "sm",
  label,
  description,
  disabled,
  ...props
}: React.ComponentProps<typeof SwitchPrimitive.Root> &
  VariantProps<typeof toggleVariants> & {
    label?: string
    description?: string
  }) {
  const id = React.useId()

  return (
    <div
      data-slot="toggle"
      data-disabled={disabled || undefined}
      className={cn("flex items-center gap-[var(--layout-gap-md)]", className)}
    >
      <SwitchPrimitive.Root
        id={id}
        className={cn(toggleVariants({ size }))}
        disabled={disabled}
        {...props}
      >
        <SwitchPrimitive.Thumb className={cn(thumbVariants({ size }))} />
      </SwitchPrimitive.Root>
      {label && (
        <label
          htmlFor={id}
          className={cn(
            "flex flex-col",
            disabled ? "cursor-default" : "cursor-pointer"
          )}
        >
          <span
            className={cn(
              "font-accent toggle-label",
              size === "sm" ? "text-content-note" : "text-content-body"
            )}
          >
            {label}
          </span>
          {description && (
            <span className="text-content-note toggle-description">
              {description}
            </span>
          )}
        </label>
      )}
    </div>
  )
}

export { Toggle, toggleVariants }

/* ================================================================
 * TOGGLE — Track, Thumb & State Styles
 * Structure (flex, size, gap, radius) lives in toggle.tsx via cva.
 * Theming (colors, borders, states) lives here via tokens.
 * ================================================================ */

/* --------------------------------
 * Track — unchecked states
 * -------------------------------- */
.toggle-track[data-state="unchecked"] {
  background: var(--background-neutral-lighter-default);
  border: var(--layout-border-thin) solid transparent;
}
.toggle-track[data-state="unchecked"]:not([data-disabled]):hover {
  background: var(--background-neutral-lighter-hover);
}
.toggle-track[data-state="unchecked"]:not([data-disabled]):active {
  background: var(--background-neutral-lighter-pressed);
}

/* --------------------------------
 * Track — checked states
 * -------------------------------- */
.toggle-track[data-state="checked"] {
  background: var(--background-brand-strong-default);
  border: var(--layout-border-thin) solid var(--border-default);
}
.toggle-track[data-state="checked"]:not([data-disabled]):hover {
  background: var(--background-brand-strong-hover);
}
.toggle-track[data-state="checked"]:not([data-disabled]):active {
  background: var(--background-brand-strong-pressed);
}

/* --------------------------------
 * Track — disabled
 * -------------------------------- */
.toggle-track[data-disabled] {
  background: var(--background-disabled);
  border: none;
  cursor: default;
  pointer-events: none;
}

/* --------------------------------
 * Focus ring (matches Checkbox/Radio/Button pattern)
 * -------------------------------- */
.toggle-track:focus-visible {
  outline: none;
  box-shadow: 0 0 0 2px var(--background-base), 0 0 0 4px var(--border-selected);
}

/* --------------------------------
 * Thumb — base
 * -------------------------------- */
.toggle-thumb {
  background: var(--text-fixed);
  box-shadow:
    0 0 1px rgba(0, 0, 0, 0.05),
    0 2px 2px rgba(0, 0, 0, 0.05),
    0 4px 3px rgba(0, 0, 0, 0.03);
  transition: translate 300ms cubic-bezier(0.34, 1.56, 0.64, 1);
}

/* Squish on press — uses global @keyframes toggle-squish */
.toggle-track:not([data-disabled]):active .toggle-thumb {
  animation: var(--animate-toggle-squish);
}

/* --------------------------------
 * Thumb — disabled
 * -------------------------------- */
.toggle-track[data-disabled] .toggle-thumb {
  background: var(--text-disabled);
  box-shadow: none;
}

/* --------------------------------
 * Text — label & description
 * -------------------------------- */
.toggle-label {
  color: var(--text-base-strong);
}
.toggle-description {
  color: var(--text-base-medium);
}

/* --------------------------------
 * Text — disabled
 * -------------------------------- */
[data-slot="toggle"][data-disabled] .toggle-label,
[data-slot="toggle"][data-disabled] .toggle-description {
  color: var(--text-disabled);
}
[data-slot="toggle"][data-disabled] label {
  cursor: default;
}
